import * as React from 'react'
import TodoTable from "./todoTable"

export default class TodoComponent extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            todos: {},
            request: Request.NONE
        }
    }

    componentDidMount() {
        alert(this.state.request)
    }

    render() {
        return (
            this.state.request === Request.SUCCESS
                ? <TodoTable/>
                : this.notSuccessHandler
            )
    }

    notSuccessHandler = () => (<div>
        {this.state.request === Request.ERROR
            ? 'There was an error retrieving TODOs'
            : (
                this.state.request === Request.PENDING
                    ? 'Loading...'
                    : 'Please search some Todos :)'
            )}
    </div>)
}
