import {Todo} from "../model/Todo";

export const searchTodos = () =>
    fetch('https://jsonplaceholsder.typicode.com/todos')
        .then(handleErrors)
        .then(response => response.json())
        .then(jsonTodos =>
            jsonTodos.map(jt => new Todo(
                jt.userId,
                jt.id,
                jt.title,
                jt.completed
            ))
        )

function handleErrors(response) {
    if (!response.ok) {
        throw Error(response.statusText);
    }
    return response;
}
